INSERT INTO `amasty_gdpr_privacy_policy` VALUES (1,'2025-04-18 21:52:32','2025-04-18 22:49:00','v1.0','<div>\n    <div style=\"clear:both\">\n        <p class=\"Header\" style=\"text-align:center; font-size:12pt\">\n            <em><span style=\"font-family:Arial; \">&#xa0;</span></em>\n        </p>\n        <p class=\"Header\">\n            &#xa0;\n        </p>\n    </div>\n    <p style=\"margin-bottom:0pt; text-align:center; line-height:115%; font-size:16pt\">\n        <strong><span style=\"font-family:Arial; font-variant:small-caps\">POLITYKA PRYWATNOCI </span></strong>\n    </p>\n    <p style=\"margin-bottom:0pt; text-align:center; line-height:115%; font-size:16pt\">\n        <strong><span\n            style=\"font-family:Arial; font-variant:small-caps\">SKLEPU INTERNETOWEGO GARDENLAWN.PL</span></strong>\n    </p>\n    <p style=\"margin-bottom:0pt; text-align:justify; line-height:115%; font-size:8pt\">\n        <strong><span style=\"font-family:Arial; font-variant:small-caps\">&#xa0;</span></strong>\n    </p>\n    <p class=\"NormalWeb\" style=\"margin-top:0pt; margin-bottom:6pt; line-height:115%; font-size:8pt\">\n        <strong><span style=\"font-family:Arial; \">SPIS TRECI: </span></strong>\n    </p>\n    <ol style=\"margin:0pt; padding-left:0pt\">\n        <li class=\"NormalWeb\"\n            style=\"margin-top:14pt; margin-left:14.97pt; margin-bottom:0pt; padding-left:6.33pt; font-family:Arial; font-size:8pt; font-variant:small-caps\">\n            POSTANOWIENIA OGLNE\n        </li>\n        <li class=\"NormalWeb\"\n            style=\"margin-top:0pt; margin-left:14.97pt; margin-bottom:0pt; padding-left:6.33pt; font-family:Arial; font-size:8pt; font-variant:small-caps\">\n            PODSTAWY PRZETWARZANIA DANYCH\n        </li>\n        <li class=\"NormalWeb\"\n            style=\"margin-top:0pt; margin-left:14.97pt; margin-bottom:0pt; padding-left:6.33pt; font-family:Arial; font-size:8pt; font-variant:small-caps\">\n            CEL, PODSTAWA I OKRES PRZETWARZANIA DANYCH W SKLEPIE INTERNETOWYM\n        </li>\n        <li class=\"NormalWeb\"\n            style=\"margin-top:0pt; margin-left:14.97pt; margin-bottom:0pt; padding-left:6.33pt; font-family:Arial; font-size:8pt; font-variant:small-caps\">\n            ODBIORCY DANYCH W SKLEPIE INTERNETOWYM\n        </li>\n        <li class=\"NormalWeb\"\n            style=\"margin-top:0pt; margin-left:14.97pt; margin-bottom:0pt; padding-left:6.33pt; font-family:Arial; font-size:8pt; font-variant:small-caps\">\n            PROFILOWANIE W SKLEPIE INTERNETOWYM\n        </li>\n        <li class=\"NormalWeb\"\n            style=\"margin-top:0pt; margin-left:14.97pt; margin-bottom:0pt; padding-left:6.33pt; font-family:Arial; font-size:8pt; font-variant:small-caps\">\n            PRAWA OSOBY, KTREJ DANE DOTYCZ\n        </li>\n        <li class=\"NormalWeb\"\n            style=\"margin-top:0pt; margin-left:14.97pt; margin-bottom:0pt; padding-left:6.33pt; font-family:Arial; font-size:8pt; font-variant:small-caps\">\n            COOKIES W SKLEPIE INTERNETOWYM I ANALITYKA\n        </li>\n        <li class=\"NormalWeb\"\n            style=\"margin-top:0pt; margin-left:14.97pt; margin-bottom:0pt; padding-left:6.33pt; font-family:Arial; font-size:8pt; font-variant:small-caps\">\n            POSTANOWIENIA KOCOWE\n        </li>\n    </ol>\n    <p style=\"margin-bottom:0pt; text-align:justify; line-height:115%; font-size:8pt\">\n        <strong><span style=\"font-family:Arial; font-variant:small-caps\">&#xa0;</span></strong>\n    </p>\n    <h1>\n        <span style=\"font-family:Arial\">1.</span><span\n        style=\"width:7.99pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">POSTANOWIENIA OGLNE </span>\n    </h1>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>1.1.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">Niniejsza polityka prywatnoci Sklepu Internetowego ma charakter informacyjny, co oznacza, e nie jest ona rdem obowizkw dla Usugobiorcw lub Klientw Sklepu Internetowego. Polityka prywatnoci zawiera przede wszystkim zasady dotyczce przetwarzania danych osobowych przez Administratora w Sklepie Internetowym, w tym podstawy, cele i okres przetwarzania danych osobowych oraz prawa osb, ktrych dane dotycz, a take informacje w zakresie stosowania w Sklepie Internetowym plikw Cookies oraz narzdzi analitycznych. </span>\n    </h2>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>1.2.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">Administratorem danych osobowych zbieranych za porednictwem Sklepu Internetowego jest MARCIN PIECHOTA prowadzcy dziaalno gospodarcz pod firm GARDEN LAWN &amp; IRRIGATION MARCIN PIECHOTA wpisany do Centralnej Ewidencji i Informacji o Dziaalnoci Gospodarczej Rzeczypospolitej Polskiej prowadzonej przez ministra waciwego do spraw gospodarki, posiadajcy: adres miejsca wykonywania dziaalnoci i adres do dorcze: ul. Namysowska 2, 46-081 Dobrze Wielki, NIP: 9910370112, REGON: 383643646, adres poczty elektronicznej: sklep@gardenlawn.pl, numer telefonu: 795-997-947  zwany dalej </span><strong><span\n        style=\"font-family:Arial; \">Administratorem</span></strong><span style=\"font-family:Arial\"> i bdcy jednoczenie Usugodawc Sklepu Internetowego i Sprzedawc.</span>\n    </h2>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>1.3.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">Dane osobowe w Sklepie Internetowym przetwarzane s przez Administratora zgodnie z obowizujcymi przepisami prawa, w szczeglnoci zgodnie z rozporzdzeniem Parlamentu Europejskiego i Rady (UE) 2016/679 z dnia 27 kwietnia 2016 r. w sprawie ochrony osb fizycznych w zwizku z przetwarzaniem danych osobowych i w sprawie swobodnego przepywu takich danych oraz uchylenia dyrektywy 95/46/WE (oglne rozporzdzenie o ochronie danych)  zwanym dalej </span><strong><span\n        style=\"font-family:Arial; \">RODO</span></strong><span\n        style=\"font-family:Arial\"> lub </span><strong><span\n        style=\"font-family:Arial; \">Rozporzdzeniem RODO</span></strong><span style=\"font-family:Arial\">. Oficjalny tekst Rozporzdzenia RODO: </span><a\n        href=\"http://eur-lex.europa.eu/legal-content/PL/TXT/?uri=CELEX%3A32016R0679\"\n        style=\"text-decoration:none\"><span class=\"Hyperlink\" style=\"font-family:Arial\">http://eur-lex.europa.eu/legal-content/PL/TXT/?uri=CELEX%3A32016R0679</span></a><span\n        style=\"font-family:Arial\"> </span>\n    </h2>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>1.4.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">Korzystanie ze Sklepu Internetowego, w tym dokonywanie zakupw jest dobrowolne. Podobnie zwizane z tym podanie danych osobowych przez korzystajcego ze Sklepu Internetowego Usugobiorc lub Klienta jest dobrowolne, z zastrzeeniem dwch wyjtkw: (1) </span><strong><span\n        style=\"font-family:Arial; \">zawieranie umw z Administratorem</span></strong><span\n        style=\"font-family:Arial\">  niepodanie w przypadkach i w zakresie wskazanym na stronie Sklepu Internetowego oraz w Regulaminie Sklepu Internetowego i niniejszej polityce prywatnoci danych osobowych niezbdnych do zawarcia i wykonania Umowy Sprzeday lub umowy o wiadczenie Usugi Elektronicznej z Administratorem skutkuje brakiem moliwoci zawarcia teje umowy. Podanie danych osobowych jest w takim wypadku wymogiem umownym i jeeli osoba, ktrej dane dotycz chce zawrze dan umow z Administratorem, to jest zobowizana do podania wymaganych danych. Kadorazowo zakres danych wymaganych do zawarcia umowy wskazany jest uprzednio na stronie Sklepu Internetowego oraz w Regulaminie Sklepu Internetowego; (2) </span><strong><span\n        style=\"font-family:Arial; \">obowizki ustawowe Administratora</span></strong><span\n        style=\"font-family:Arial\">  podanie danych osobowych jest wymogiem ustawowym wynikajcym z powszechnie obowizujcych przepisw prawa nakadajcych na Administratora obowizek przetwarzania danych osobowych (np. przetwarzanie danych w celu prowadzenia ksig podatkowych lub rachunkowych) i brak ich podania uniemoliwi Administratorowi wykonanie tyche obowizkw. </span>\n    </h2>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>1.5.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">Administrator dokada szczeglnej starannoci w celu ochrony interesw osb, ktrych przetwarzane przez niego dane osobowe dotycz, a w szczeglnoci jest odpowiedzialny i zapewnia, e zbierane przez niego dane s: (1) przetwarzane zgodnie z prawem; (2) zbierane dla oznaczonych, zgodnych z prawem celw i niepoddawane dalszemu przetwarzaniu niezgodnemu z tymi celami; (3) merytorycznie poprawne i adekwatne w stosunku do celw, w jakich s przetwarzane; (4) przechowywane w postaci umoliwiajcej identyfikacj osb, ktrych dotycz, nie duej ni jest to niezbdne do osignicia celu przetwarzania oraz (5) przetwarzane</span><span\n        style=\"font-family:Arial\">&#xa0; </span><span style=\"font-family:Arial\">w</span><span\n        style=\"font-family:Arial\">&#xa0; </span><span style=\"font-family:Arial\">sposb</span><span\n        style=\"font-family:Arial\">&#xa0; </span><span style=\"font-family:Arial\">zapewniajcy</span><span\n        style=\"font-family:Arial\">&#xa0; </span><span style=\"font-family:Arial\">odpowiednie</span><span\n        style=\"font-family:Arial\">&#xa0; </span><span style=\"font-family:Arial\">bezpieczestwo danych</span><span\n        style=\"font-family:Arial\">&#xa0; </span><span style=\"font-family:Arial\">osobowych,</span><span\n        style=\"font-family:Arial\">&#xa0; </span><span style=\"font-family:Arial\">w</span><span\n        style=\"font-family:Arial\">&#xa0; </span><span style=\"font-family:Arial\">tym</span><span\n        style=\"font-family:Arial\">&#xa0; </span><span style=\"font-family:Arial\">ochron</span><span\n        style=\"font-family:Arial\">&#xa0; </span><span style=\"font-family:Arial\">przed niedozwolonym</span><span\n        style=\"font-family:Arial\">&#xa0; </span><span style=\"font-family:Arial\">lub</span><span\n        style=\"font-family:Arial\">&#xa0; </span><span style=\"font-family:Arial\">niezgodnym</span><span\n        style=\"font-family:Arial\">&#xa0; </span><span style=\"font-family:Arial\">z</span><span\n        style=\"font-family:Arial\">&#xa0; </span><span style=\"font-family:Arial\">prawem</span><span\n        style=\"font-family:Arial\">&#xa0; </span><span style=\"font-family:Arial\">przetwarzaniem</span><span\n        style=\"font-family:Arial\">&#xa0; </span><span style=\"font-family:Arial\">oraz</span><span\n        style=\"font-family:Arial\">&#xa0; </span><span style=\"font-family:Arial\">przypadkow</span><span\n        style=\"font-family:Arial\">&#xa0; </span><span style=\"font-family:Arial\">utrat,</span><span\n        style=\"font-family:Arial\">&#xa0; </span><span style=\"font-family:Arial\">zniszczeniem</span><span\n        style=\"font-family:Arial\">&#xa0; </span><span style=\"font-family:Arial\">lub uszkodzeniem, za pomoc odpowiednich rodkw technicznych lub organizacyjnych.</span>\n    </h2>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>1.6.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">Uwzgldniajc charakter, zakres, kontekst i cele przetwarzania oraz ryzyko naruszenia praw lub wolnoci osb fizycznych o rnym prawdopodobiestwie i wadze zagroenia, Administrator wdraa odpowiednie rodki techniczne i organizacyjne, aby przetwarzanie odbywao si zgodnie z niniejszym rozporzdzeniem i aby mc to wykaza. rodki te s w razie potrzeby poddawane przegldom i uaktualniane. Administrator stosuje rodki techniczne zapobiegajce pozyskiwaniu i modyfikowaniu przez osoby nieuprawnione, danych osobowych przesyanych drog elektroniczn. </span>\n    </h2>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>1.7.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">Wszelkie sowa, wyraenia i akronimy wystpujce w niniejszej polityce prywatnoci i rozpoczynajce si du liter (np. </span><strong><span\n        style=\"font-family:Arial; \">Sprzedawca</span></strong><span\n        style=\"font-family:Arial\">, </span><strong><span\n        style=\"font-family:Arial; \">Sklep Internetowy</span></strong><span\n        style=\"font-family:Arial\">, </span><strong><span\n        style=\"font-family:Arial; \">Usuga Elektroniczna</span></strong><span style=\"font-family:Arial\">) naley rozumie zgodnie z ich definicj zawart w Regulaminie Sklepu Internetowego dostpnym na stronach Sklepu Internetowego.</span>\n    </h2>\n    <h1>\n        <span style=\"font-family:Arial\">2.</span><span\n        style=\"width:7.99pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">PODSTAWY PRZETWARZANIA DANYCH </span>\n    </h1>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>2.1.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">Administrator uprawniony jest do przetwarzania danych osobowych w przypadkach, gdy  i w takim zakresie, w jakim  speniony jest co najmniej jeden z poniszych warunkw: (1) osoba, ktrej dane dotycz wyrazia zgod na przetwarzanie swoich danych osobowych w jednym lub wikszej liczbie okrelonych celw; (2) przetwarzanie jest niezbdne do wykonania umowy, ktrej stron jest osoba, ktrej dane dotycz, lub do podjcia dziaa na danie osoby, ktrej dane dotycz, przed zawarciem umowy; (3) przetwarzanie jest niezbdne do wypenienia obowizku prawnego cicego na Administratorze; lub (4) przetwarzanie jest niezbdne do celw wynikajcych z prawnie uzasadnionych interesw realizowanych przez Administratora lub przez stron trzeci, z wyjtkiem sytuacji, w ktrych nadrzdny charakter wobec tych interesw maj interesy lub podstawowe prawa i wolnoci osoby, ktrej dane dotycz, wymagajce ochrony danych osobowych, w szczeglnoci gdy osoba, ktrej dane dotycz, jest dzieckiem.</span>\n    </h2>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>2.2.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">Przetwarzanie danych osobowych przez Administratora wymaga kadorazowo zaistnienia co najmniej jednej z podstaw wskazanych w pkt. 2.1 polityki prywatnoci. Konkretne podstawy przetwarzania danych osobowych Usugobiorcw i Klientw Sklepu Internetowego przez Administratora s wskazane w kolejnym punkcie polityki prywatnoci  w odniesieniu do danego celu przetwarzania danych osobowych przez Administratora. </span>\n    </h2>\n    <h1>\n        <span style=\"font-family:Arial\">3.</span><span\n        style=\"width:7.99pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">CEL, PODSTAWA I OKRES PRZETWARZANIA DANYCH W SKLEPIE INTERNETOWYM </span>\n    </h1>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>3.1.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">Kadorazowo cel, podstawa i okres oraz odbiorcy danych osobowych przetwarzanych przez Administratora wynika z dziaa podejmowanych przez danego Usugobiorc lub Klienta w Sklepie Internetowym lub przez Administratora. Przykadowo, jeeli Klient decyduje si na dokonanie zakupw w Sklepie Internetowym i wybierze odbir osobisty zakupionego Produktu zamiast przesyki kurierskiej, to jego dane osobowe bd przetwarzane w celu wykonania zawartej Umowy Sprzeday, ale nie bd ju udostpniane przewonikowi realizujcemu przesyki na zlecenie Administratora.</span><span\n        style=\"font-family:Arial\">&#xa0; </span>\n    </h2>\n    <h2 style=\"margin-top:2pt; margin-bottom:4.8pt\">\n        <span style=\"font-family:Arial\"><strong>3.2.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">Administrator moe przetwarza dane osobowe w ramach Sklepu Internetowego w nastpujcych celach, na podstawach oraz w okresach wskazanych w poniszej tabeli: </span>\n    </h2>\n    <p style=\"margin-bottom:0pt; line-height:115%; font-size:5pt\">\n        <span style=\"font-family:Arial\">&#xa0;</span>\n    </p>\n    <table style=\"margin-left:35.2pt; margin-bottom:0pt; border:0.75pt solid #000000; border-collapse:collapse\">\n        <tr style=\"height:30.8pt\">\n            <td style=\"width:138.05pt; border-right:0.75pt solid #000000; border-bottom:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:middle; background-color:#e2efd9\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; text-align:center; line-height:normal; font-size:9pt\">\n                    <strong><span style=\"font-family:Arial; \">Cel przetwarzania danych</span></strong>\n                </p>\n            </td>\n            <td style=\"width:152.2pt; border-right:0.75pt solid #000000; border-left:0.75pt solid #000000; border-bottom:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:middle; background-color:#e2efd9\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; text-align:center; line-height:normal; font-size:9pt\">\n                    <strong><span style=\"font-family:Arial; \">Podstawa prawna przetwarzania danych</span></strong>\n                </p>\n            </td>\n            <td style=\"width:153.8pt; border-left:0.75pt solid #000000; border-bottom:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:middle; background-color:#e2efd9\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; text-align:center; line-height:normal; font-size:9pt\">\n                    <strong><span style=\"font-family:Arial; \">Okres przechowywania danych</span></strong>\n                </p>\n            </td>\n        </tr>\n        <tr>\n            <td style=\"width:138.05pt; border-top:0.75pt solid #000000; border-right:0.75pt solid #000000; border-bottom:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Wykonanie Umowy Sprzeday lub umowy o wiadczenie Usugi Elektronicznej lub podjcie dziaa na danie osoby, ktrej dane dotycz, przed zawarciem w/w umw</span>\n                </p>\n            </td>\n            <td style=\"width:152.2pt; border:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Artyku 6 ust. 1 lit. b) Rozporzdzenia RODO (wykonanie umowy)  przetwarzanie jest niezbdne do wykonania umowy, ktrej stron jest osoba, ktrej dane dotycz, lub do podjcia dziaa na danie osoby, ktrej dane dotycz, przed zawarciem umowy</span>\n                </p>\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial; color:#ff0000\">&#xa0;</span>\n                </p>\n            </td>\n            <td style=\"width:153.8pt; border-top:0.75pt solid #000000; border-left:0.75pt solid #000000; border-bottom:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Dane s przechowywane przez okres niezbdny do wykonania, rozwizania lub wyganicia w inny sposb zawartej Umowy Sprzeday lub umowy o wiadczenie Usugi Elektronicznej. </span>\n                </p>\n            </td>\n        </tr>\n        <tr>\n            <td style=\"width:138.05pt; border-top:0.75pt solid #000000; border-right:0.75pt solid #000000; border-bottom:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Marketing bezporedni </span>\n                </p>\n            </td>\n            <td style=\"width:152.2pt; border:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Artyku 6 ust. 1 lit. f) Rozporzdzenia RODO (prawnie uzasadniony interes administratora)  przetwarzanie jest niezbdne do celw wynikajcych z prawnie uzasadnionych interesw Administratora  polegajcych na dbaniu o interesy i dobry wizerunek Administratora, jego Sklepu Internetowego oraz deniu do sprzeday Produktw</span>\n                </p>\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">&#xa0;</span>\n                </p>\n            </td>\n            <td style=\"width:153.8pt; border-top:0.75pt solid #000000; border-left:0.75pt solid #000000; border-bottom:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Dane s przechowywane przez okres istnienia prawnie uzasadnionego interesu realizowanego przez Administratora, nie duej jednak ni przez okres przedawnienia roszcze Administratora w stosunku do osoby, ktrej dane dotycz, z tytuu prowadzonej przez Administratora dziaalnoci gospodarczej. Okres przedawnienia okrelaj przepisy prawa, w szczeglnoci Kodeksu Cywilnego (podstawowy termin przedawnienia dla roszcze zwizanych z prowadzeniem dziaalnoci gospodarczej wynosi trzy lata, a dla Umowy Sprzeday dwa lata).</span>\n                </p>\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Administrator nie moe przetwarza danych w celu marketingu bezporedniego w przypadku wyraenia skutecznego sprzeciwu w tym zakresie przez osob, ktrej dane dotycz.</span>\n                </p>\n            </td>\n        </tr>\n        <tr>\n            <td style=\"width:138.05pt; border-top:0.75pt solid #000000; border-right:0.75pt solid #000000; border-bottom:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Marketing </span>\n                </p>\n            </td>\n            <td style=\"width:152.2pt; border:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Artyku 6 ust. 1 lit. a) Rozporzdzenia RODO (zgoda)  osoba, ktrej dane dotycz, wyrazia zgod na przetwarzanie swoich danych osobowych w celach marketingowych przez Administratora</span>\n                </p>\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">&#xa0;</span>\n                </p>\n            </td>\n            <td style=\"width:153.8pt; border-top:0.75pt solid #000000; border-left:0.75pt solid #000000; border-bottom:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Dane przechowywane s do momentu wycofania zgody przez osob, ktrej dane dotycz na dalsze przetwarzanie jej danych w tym celu.</span>\n                </p>\n            </td>\n        </tr>\n        <tr>\n            <td style=\"width:138.05pt; border-top:0.75pt solid #000000; border-right:0.75pt solid #000000; border-bottom:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Wyraenie przez Klienta opinii o zawartej Umowie Sprzeday</span>\n                </p>\n            </td>\n            <td style=\"width:152.2pt; border:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Artyku 6 ust. 1 lit. a) Rozporzdzenia RODO  osoba, ktrej dane dotycz, wyrazia zgod na przetwarzanie swoich danych osobowych w celu wyraenia opinii</span>\n                </p>\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">&#xa0;</span>\n                </p>\n            </td>\n            <td style=\"width:153.8pt; border-top:0.75pt solid #000000; border-left:0.75pt solid #000000; border-bottom:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Dane przechowywane s do momentu wycofania zgody przez osob, ktrej dane dotycz na dalsze przetwarzanie jej danych w tym celu.</span>\n                </p>\n            </td>\n        </tr>\n        <tr>\n            <td style=\"width:138.05pt; border-top:0.75pt solid #000000; border-right:0.75pt solid #000000; border-bottom:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Prowadzenie ksig podatkowych </span>\n                </p>\n            </td>\n            <td style=\"width:152.2pt; border:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Artyku 6 ust. 1 lit. c) Rozporzdzenia RODO w zw. z art. 86  1 Ordynacji podatkowej tj. z dnia 17 stycznia 2017 r. (Dz.U. z 2017 r. poz. 201 ze zm.) przetwarzanie jest niezbdne do wypenienia obowizku prawnego cicego na Administratorze</span>\n                </p>\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">&#xa0;</span>\n                </p>\n            </td>\n            <td style=\"width:153.8pt; border-top:0.75pt solid #000000; border-left:0.75pt solid #000000; border-bottom:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Dane s przechowywane przez okres wymagany przepisami prawa nakazujcymi Administratorowi przechowywanie ksig podatkowych (do czasu upywu okresu przedawnienia zobowizania podatkowego, chyba e ustawy podatkowe stanowi inaczej).</span>\n                </p>\n            </td>\n        </tr>\n        <tr>\n            <td style=\"width:138.05pt; border-top:0.75pt solid #000000; border-right:0.75pt solid #000000; border-bottom:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Ustalenie, dochodzenie lub obrona roszcze jakie moe podnosi Administrator lub jakie mog by podnoszone wobec Administratora </span>\n                </p>\n            </td>\n            <td style=\"width:152.2pt; border:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal\">\n                    <span style=\"font-family:Arial; font-size:9pt\">Artyku 6 ust. 1 lit. f) Rozporzdzenia RODO (prawnie uzasadniony interes administratora)  przetwarzanie jest niezbdne do celw wynikajcych z prawnie uzasadnionych interesw Administratora  polegajcych na ustaleniu, dochodzeniu lub obronie roszcze,</span><span\n                    style=\"font-family:Arial\"> </span><span style=\"font-family:Arial; font-size:9pt\">jakie moe podnosi Administrator lub jakie mog by podnoszone wobec Administratora</span>\n                </p>\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">&#xa0;</span>\n                </p>\n            </td>\n            <td style=\"width:153.8pt; border-top:0.75pt solid #000000; border-left:0.75pt solid #000000; border-bottom:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Dane s przechowywane przez okres istnienia prawnie uzasadnionego interesu realizowanego przez Administratora, nie duej jednak ni przez okres przedawnienia roszcze jakie mog by podnoszone wobec Administratora (podstawowy termin przedawnienia dla roszcze wobec Administratora wynosi sze lat).</span>\n                </p>\n            </td>\n        </tr>\n        <tr>\n            <td style=\"width:138.05pt; border-top:0.75pt solid #000000; border-right:0.75pt solid #000000; border-bottom:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Korzystanie ze strony Sklepu Internetowego i zapewnienie jej prawidowego dziaania</span>\n                </p>\n            </td>\n            <td style=\"width:152.2pt; border:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Artyku 6 ust. 1 lit. f) Rozporzdzenia RODO (prawnie uzasadniony interes administratora)  przetwarzanie jest niezbdne do celw wynikajcych z prawnie uzasadnionych interesw Administratora  polegajcych na prowadzeniu i utrzymaniu strony Sklepu Internetowego</span>\n                </p>\n            </td>\n            <td style=\"width:153.8pt; border-top:0.75pt solid #000000; border-left:0.75pt solid #000000; border-bottom:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Dane s przechowywane przez okres istnienia prawnie uzasadnionego interesu realizowanego przez Administratora, nie duej jednak ni przez okres przedawnienia roszcze Administratora w stosunku do osoby, ktrej dane dotycz, z tytuu prowadzonej przez Administratora dziaalnoci gospodarczej. Okres przedawnienia okrelaj przepisy prawa, w szczeglnoci Kodeksu Cywilnego (podstawowy termin przedawnienia dla roszcze zwizanych z prowadzeniem dziaalnoci gospodarczej wynosi trzy lata, a dla Umowy Sprzeday dwa lata).</span>\n                </p>\n            </td>\n        </tr>\n        <tr>\n            <td style=\"width:138.05pt; border-top:0.75pt solid #000000; border-right:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Prowadzenie statystyk i analiza ruchu w Sklepie Internetowym</span>\n                </p>\n            </td>\n            <td style=\"width:152.2pt; border-top:0.75pt solid #000000; border-right:0.75pt solid #000000; border-left:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Artyku 6 ust. 1 lit. f) Rozporzdzenia RODO (prawnie uzasadniony interes administratora)  przetwarzanie jest niezbdne do celw wynikajcych z prawnie uzasadnionych interesw Administratora  polegajcych na prowadzeniu statystyk i analizie ruchu w Sklepie Internetowym celem poprawy funkcjonowania Sklepu Internetowego i zwikszenia sprzeday Produktw</span>\n                </p>\n            </td>\n            <td style=\"width:153.8pt; border-top:0.75pt solid #000000; border-left:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-top:4.8pt; margin-bottom:4.8pt; line-height:normal; font-size:9pt\">\n                    <span style=\"font-family:Arial\">Dane s przechowywane przez okres istnienia prawnie uzasadnionego interesu realizowanego przez Administratora, nie duej jednak ni przez okres przedawnienia roszcze Administratora w stosunku do osoby, ktrej dane dotycz, z tytuu prowadzonej przez Administratora dziaalnoci gospodarczej. Okres przedawnienia okrelaj przepisy prawa, w szczeglnoci Kodeksu Cywilnego (podstawowy termin przedawnienia dla roszcze zwizanych z prowadzeniem dziaalnoci gospodarczej wynosi trzy lata, a dla Umowy Sprzeday dwa lata).</span>\n                </p>\n            </td>\n        </tr>\n    </table>\n    <h1>\n        <span style=\"font-family:Arial\">4.</span><span\n        style=\"width:7.99pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">ODBIORCY DANYCH W SKLEPIE INTERNETOWYM</span>\n    </h1>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>4.1.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">Dla prawidowego funkcjonowania Sklepu Internetowego, w tym dla realizacji zawieranych Umw Sprzeday konieczne jest korzystanie przez Administratora z usug podmiotw zewntrznych (takich jak np. dostawca oprogramowania, kurier czy podmiot obsugujcy patnoci). Administrator korzysta wycznie z usug takich podmiotw przetwarzajcych, ktrzy zapewniaj wystarczajce gwarancje wdroenia odpowiednich rodkw technicznych i organizacyjnych, tak by przetwarzanie speniao wymogi Rozporzdzenia RODO i chronio prawa osb, ktrych dane dotycz. </span>\n    </h2>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>4.2.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">Dane osobowe mog by przekazywane przez Administratora do pastwa trzeciego, przy czym Administrator zapewnia, e w takim przypadku odbywa si to bdzie w stosunku do pastwa zapewniajcego odpowiedni stopie ochrony  zgodny z Rozporzdzeniem RODO, a w przypadku innych pastw, e przekazanie odbywa si bdzie na podstawie standardowych klauzul ochrony danych. Administrator zapewnia, e osoba, ktrej dane dotycz ma moliwo uzyskania kopii swoich danych. Administrator przekazuje zebrane dane osobowe jedynie w przypadku oraz w zakresie niezbdnym do zrealizowania danego celu przetwarzania danych zgodnego z niniejsz polityk prywatnoci.</span>\n    </h2>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>4.3.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">Przekazanie danych przez Administratora nie nastpuje w kadym wypadku i nie do wszystkich wskazanych w polityce prywatnoci odbiorcw lub kategorii odbiorcw  Administrator przekazuje dane wycznie wtedy, gdy jest to niezbdne do realizacji danego celu przetwarzania danych osobowych i tylko w zakresie niezbdnym do jego zrealizowania. Przykadowo, jeeli Klient korzysta z odbioru osobistego, to jego dane nie bd przekazywane przewonikowi wsppracujcemu z Administratorem. </span>\n    </h2>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>4.4.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">Dane osobowe Usugobiorcw i Klientw Sklepu Internetowego mog by przekazywane nastpujcym odbiorcom lub kategoriom odbiorcw: </span>\n    </h2>\n    <p class=\"111pkt\">\n        <span style=\"font-family:Arial\">4.4.1.</span><span\n        style=\"width:12.93pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><strong><span\n        style=\"font-family:Arial; \">przewonicy / spedytorzy / brokerzy </span></strong><a\n        id=\"_Hlk40877743\"><strong><span style=\"font-family:Arial; \">kurierscy /</span></strong><span\n        style=\"font-family:Arial\"> </span><strong><span style=\"font-family:Arial; \">podmioty obsugujce magazyn i/lub proces wysyki</span></strong><span\n        style=\"font-family:Arial\">  w przypadku Klienta, ktry korzysta w Sklepie Internetowym ze sposobu dostawy Produktu przesyk pocztow lub przesyk kuriersk, Administrator udostpnia zebrane dane osobowe Klienta wybranemu przewonikowi, spedytorowi lub porednikowi realizujcemu przesyki na zlecenie Administratora, a jeeli wysyka nastpuje z magazynu zewntrznego  podmiotowi obsugujcemu magazyn i/lub proces wysyki  w zakresie niezbdnym do zrealizowania dostawy Produktu Klientowi</span></a><span\n        style=\"font-family:Arial\">.</span>\n    </p>\n    <p class=\"111pkt\">\n        <span style=\"font-family:Arial\">4.4.2.</span><span\n        style=\"width:12.93pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><strong><span\n        style=\"font-family:Arial; \">podmioty obsugujce patnoci elektroniczne lub kart patnicz</span></strong><span\n        style=\"font-family:Arial\">  w przypadku Klienta, ktry korzysta w Sklepie Internetowym ze sposobu patnoci elektronicznych lub kart patnicz, Administrator udostpnia zebrane dane osobowe Klienta wybranemu podmiotowi obsugujcemu powysze patnoci w Sklepie Internetowym na zlecenie Administratora w zakresie niezbdnym do obsugi patnoci realizowanej przez Klienta. </span>\n    </p>\n    <p class=\"111pkt\">\n        <span style=\"font-family:Arial\">4.4.3.</span><span\n        style=\"width:12.93pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><strong><span\n        style=\"font-family:Arial; \">podmioty kredytujce / leasingodawcy</span></strong><span\n        style=\"font-family:Arial\">  w przypadku Klienta, ktry korzysta w Sklepie Internetowym ze sposobu patnoci w systemie ratalnym lub patnoci leasingowej Administrator udostpnia zebrane dane osobowe Klienta wybranemu kredytodawcy lub leasingodawcy obsugujcemu powysze patnoci w Sklepie Internetowym na zlecenie Administratora w zakresie niezbdnym do obsugi patnoci realizowanej przez Klienta.</span>\n    </p>\n    <p class=\"111pkt\">\n        <span style=\"font-family:Arial\">4.4.4.</span><span\n        style=\"width:12.93pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><strong><span\n        style=\"font-family:Arial; \">dostawcy usug zaopatrujcy Administratora w rozwizania techniczne, informatyczne oraz organizacyjne, umoliwiajce Administratorowi prowadzenie dziaalnoci gospodarczej, w tym Sklepu Internetowego i wiadczonych za jego porednictwem Usug Elektronicznych</span></strong><span\n        style=\"font-family:Arial\"> (w szczeglnoci dostawcy oprogramowania komputerowego do prowadzenia Sklepu Internetowego, dostawcy poczty elektronicznej i hostingu oraz dostawcy oprogramowania do zarzdzania firm i udzielania pomocy technicznej Administratorowi)  Administrator udostpnia zebrane dane osobowe Klienta wybranemu dostawcy dziaajcemu na jego zlecenie jedynie w przypadku oraz w zakresie niezbdnym do zrealizowania danego celu przetwarzania danych zgodnego z niniejsz polityk prywatnoci.</span>\n    </p>\n    <p class=\"111pkt\">\n        <span style=\"font-family:Arial\">4.4.5.</span><span\n        style=\"width:12.93pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><strong><span\n        style=\"font-family:Arial; \">dostawcy usug ksigowych, prawnych i doradczych zapewniajcy Administratorowi wsparcie ksigowe, prawne lub doradcze </span></strong><span\n        style=\"font-family:Arial\">(w szczeglnoci biuro ksigowe, kancelaria prawna lub firma windykacyjna)  Administrator udostpnia zebrane dane osobowe Klienta wybranemu dostawcy dziaajcemu na jego zlecenie jedynie w przypadku oraz w zakresie niezbdnym do zrealizowania danego celu przetwarzania danych zgodnego z niniejsz polityk prywatnoci.</span>\n    </p>\n    <p class=\"111pkt\">\n        <span style=\"font-family:Arial\">4.4.6.</span><span\n        style=\"width:12.93pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><a\n        id=\"_Hlk40877781\"><strong><span style=\"font-family:Arial; \">dostawcy umieszczonych na stronie Sklepu Internetowego wtyczek spoecznociowych, skryptw oraz innych podobnych narzdzi umoliwiajcych przegldarce osoby odwiedzajcej stron Sklepu Internetowego pobieranie treci od dostawcw wspomnianych wtyczek</span></strong><span\n        style=\"font-family:Arial\"> (np. logowanie za pomoc danych logowania do serwisu spoecznociowego) i przekazywanie w tym celu tym dostawcom danych osobowych osoby odwiedzajcej</span></a><span\n        style=\"font-family:Arial\">, w tym rwnie: </span>\n    </p>\n    <p class=\"1111pkt\" style=\"margin-left:42.5pt\">\n        <span style=\"font-family:Arial\">4.4.6.1.</span><span\n        style=\"width:12.48pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><strong><span\n        style=\"font-family:Arial; \">Meta Platforms Ireland Ltd.  </span></strong><span\n        style=\"font-family:Arial\">Administrator korzysta na stronie Sklepu Internetowego z wtyczek spoecznociowych serwisu Facebook i Instagram (np. przycisk Lubi to!, Udostpnij lub logowanie za pomoc danych logowania do serwisu Facebook) oraz Instagram, oraz komunikatora WhatsApp do prowadzenia korespondencji - i w zwizku z tym gromadzi i udostpnia dane osobowe Usugobiorcy korzystajcego ze strony Sklepu Internetowego do Meta Platforms Ireland Ltd. (4 Grand Canal Square, Grand Canal Harbour, Dublin 2 Ireland) w zakresie i zgodnie z zasadami prywatnoci dostpnymi  w przypadku Facebooka  tutaj: https://www.facebook.com/about/privacy/ oraz  w przypadku Instagrama  tutaj: https://help.instagram.com/519522125107875/?helpref=hc_fnav  oraz  w przypadku WhatsApp  tutaj: https://www.whatsapp.com/legal/privacy-policy-eea?lang=pl_PL  (dane te obejmuj informacje o dziaaniach na stronie Sklepu Internetowego  w tym informacje o urzdzeniu, odwiedzanych witrynach, zakupach, wywietlanych reklamach oraz sposobie korzystania z usug  niezalenie od tego, czy Usugobiorca ma konto na Facebooku, Instagramie, WhatsApp i czy jest zalogowany do Facebooka, Instagrama lub Whatsapp).</span>\n    </p>\n    <h1>\n        <span style=\"font-family:Arial\">5.</span><span\n        style=\"width:7.99pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">PROFILOWANIE W SKLEPIE INTERNETOWYM</span>\n    </h1>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>5.1.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">Rozporzdzenie RODO nakada na Administratora obowizek informowania o zautomatyzowanym podejmowaniu decyzji, w tym o profilowaniu, o ktrym mowa w art. 22 ust. 1 i 4 Rozporzdzenia RODO, oraz  przynajmniej w tych przypadkach  istotne informacje o zasadach ich podejmowania, a take o znaczeniu i przewidywanych konsekwencjach takiego przetwarzania dla osoby, ktrej dane dotycz. Majc to na uwadze, Administrator podaje w tym punkcie polityki prywatnoci informacje dotyczce moliwego profilowania. </span>\n    </h2>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>5.2.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">Administrator moe korzysta w Sklepie Internetowym z profilowania do celw marketingu bezporedniego, ale decyzje podejmowane na jego podstawie przez Administratora nie dotycz zawarcia lub odmowy zawarcia Umowy Sprzeday czy te moliwoci korzystania z Usug Elektronicznych w Sklepie Internetowym. Efektem korzystania z profilowania w Sklepie Internetowym moe by np. przyznanie danej osobie rabatu, przesanie jej kodu rabatowego, przypomnienie o niedokoczonych zakupach, przesanie propozycji Produktu, ktry moe odpowiada zainteresowaniom lub preferencjom danej osoby lub te zaproponowanie lepszych warunkw w porwnaniu do standardowej oferty Sklepu Internetowego. Mimo profilowania to dana osoba podejmuje swobodnie decyzj, czy bdzie chciaa skorzysta z otrzymanego w ten sposb rabatu, czy te lepszych warunkw i dokona zakupu w Sklepie Internetowym.</span>\n    </h2>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>5.3.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">Profilowanie w Sklepie Internetowym polega na automatycznej analizie lub prognozie zachowania danej osoby na stronie Sklepu Internetowego np. poprzez dodanie konkretnego Produktu do koszyka, przegldanie strony konkretnego Produktu w Sklepie Internetowym czy te poprzez analiz dotychczasowej historii dokonanych zakupw w Sklepie Internetowym. Warunkiem takiego profilowania jest posiadanie przez Administratora danych osobowych danej osoby, aby mc jej nastpnie przesa np. kod rabatowy. </span>\n    </h2>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>5.4.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">Osoba, ktrej dane dotycz, ma prawo do tego, by nie podlega decyzji, ktra opiera si wycznie na zautomatyzowanym przetwarzaniu, w tym profilowaniu, i wywouje wobec tej osoby skutki prawne lub w podobny sposb istotnie na ni wpywa.</span>\n    </h2>\n    <h1>\n        <span style=\"font-family:Arial\">6.</span><span\n        style=\"width:7.99pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">PRAWA OSOBY, KTREJ DANE DOTYCZ </span>\n    </h1>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>6.1.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><strong><span\n        style=\"font-family:Arial; \">Prawo dostpu, sprostowania, ograniczenia, usunicia lub przenoszenia </span></strong><span\n        style=\"font-family:Arial\"> osoba, ktrej dane dotycz, ma prawo dania od Administratora dostpu do swoich danych osobowych, ich sprostowania, usunicia (prawo do bycia zapomnianym) lub ograniczenia przetwarzania oraz ma prawo do wniesienia sprzeciwu wobec przetwarzania, a take ma prawo do przenoszenia swoich danych. Szczegowe warunki wykonywania wskazanych wyej praw wskazane s w art. 15-21 Rozporzdzenia RODO. </span>\n    </h2>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>6.2.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><strong><span\n        style=\"font-family:Arial; \">Prawo do cofnicia zgody w dowolnym momencie</span></strong><span\n        style=\"font-family:Arial\">  osoba, ktrej dane przetwarzane s przez Administratora na podstawie wyraonej zgody (na podstawie art. 6 ust. 1 lit. a) lub art. 9 ust. 2 lit. a) Rozporzdzenia RODO), to ma ona prawo do cofnicia zgody w dowolnym momencie bez wpywu na zgodno z prawem przetwarzania, ktrego dokonano na podstawie zgody przed jej cofniciem.</span>\n    </h2>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>6.3.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><strong><span\n        style=\"font-family:Arial; \">Prawo wniesienia skargi do organu nadzorczego</span></strong><span\n        style=\"font-family:Arial\">  osoba, ktrej dane przetwarzane s przez Administratora, ma prawo wniesienia skargi do organu nadzorczego w sposb i trybie okrelonym w przepisach Rozporzdzenia RODO oraz prawa polskiego, w szczeglnoci ustawy o ochronie danych osobowych. Organem nadzorczym w Polsce jest Prezes Urzdu Ochrony Danych Osobowych. </span>\n    </h2>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>6.4.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><strong><span\n        style=\"font-family:Arial; \">Prawo do sprzeciwu</span></strong><span style=\"font-family:Arial\">  osoba, ktrej dane dotycz, ma prawo w dowolnym momencie wnie sprzeciw  z przyczyn zwizanych z jej szczegln sytuacj  wobec przetwarzania dotyczcych jej danych osobowych opartego na art. 6 ust. 1 lit. e) (interes lub zadania publiczne) lub f) (prawnie uzasadniony interes administratora), w tym profilowania na podstawie tych przepisw. Administratorowi w takim przypadku nie wolno ju przetwarza tych danych osobowych, chyba e wykae on istnienie wanych prawnie uzasadnionych podstaw do przetwarzania, nadrzdnych wobec interesw, praw i wolnoci osoby, ktrej dane dotycz, lub podstaw do ustalenia, dochodzenia lub obrony roszcze.</span>\n    </h2>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>6.5.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><strong><span\n        style=\"font-family:Arial; \">Prawo do sprzeciwu dot. marketingu bezporedniego </span></strong><span\n        style=\"font-family:Arial\"> jeeli dane osobowe s przetwarzane na potrzeby marketingu bezporedniego, osoba, ktrej dane dotycz, ma prawo w dowolnym momencie wnie sprzeciw wobec przetwarzania dotyczcych jej danych osobowych na potrzeby takiego marketingu, w tym profilowania, w zakresie, w jakim przetwarzanie jest zwizane z takim marketingiem bezporednim.</span>\n    </h2>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>6.6.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">W celu realizacji uprawnie, o ktrych mowa w niniejszym punkcie polityki prywatnoci, mona kontaktowa si z Administratorem poprzez przesanie stosownej wiadomoci pisemnie lub poczt elektroniczn na adres Administratora wskazany na wstpie polityki prywatnoci lub korzystajc z formularza kontaktowego dostpnego na stronie Sklepu Internetowego. </span>\n    </h2>\n    <h1>\n        <span style=\"font-family:Arial\">7.</span><span\n        style=\"width:7.99pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">COOKIES W SKLEPIE INTERNETOWYM I ANALITYKA</span>\n    </h1>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>7.1.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">Pliki Cookies (ciasteczka) s to niewielkie informacje tekstowe w postaci plikw tekstowych, wysyane przez serwer i zapisywane po stronie osoby odwiedzajcej stron Sklepu Internetowego (np. na dysku twardym komputera, laptopa, czy te na karcie pamici smartfonu  w zalenoci z jakiego urzdzenia korzysta odwiedzajcy nasz Sklep Internetowy). Szczegowe informacje dot. plikw Cookies, a take histori ich powstania mona znale m. in. tutaj: https://pl.wikipedia.org/wiki/HTTP_cookie.</span>\n    </h2>\n    <h2>\n        <span style=\"font-family:Arial\"><strong>7.2.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">Pliki Cookies, ktre mog by wysyane przez stron Sklepu Internetowego mona podzieli na rne rodzaje, wedug nastpujcych kryteriw:</span>\n    </h2>\n    <table style=\"margin-left:35.2pt; margin-bottom:0pt; border:0.75pt solid #000000; border-collapse:collapse\">\n        <tr>\n            <td style=\"width:143.05pt; border-right:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top; background-color:#e2efd9\">\n                <p style=\"margin-top:7.2pt; margin-bottom:7.2pt; line-height:115%; font-size:9pt\">\n                    <strong><span style=\"font-family:Arial; \">Ze wzgldu na ich dostawc</span></strong><span\n                    style=\"font-family:Arial\">: </span>\n                </p>\n                <ol class=\"awlist1\" style=\"margin:0pt; padding-left:0pt\">\n                    <li style=\"margin-top:7.2pt; margin-left:14.55pt; margin-bottom:7.2pt; text-indent:-14.55pt; line-height:115%; font-family:Arial; font-size:9pt\">\n                        <span\n                            style=\"width:6.55pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0; </span>wasne\n                        (tworzone przez stron Sklepu Internetowego Administratora) oraz\n                    </li>\n                    <li style=\"margin-top:7.2pt; margin-left:14.55pt; margin-bottom:7.2pt; text-indent:-14.55pt; line-height:115%; font-family:Arial; font-size:9pt\">\n                        <span\n                            style=\"width:6.55pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0; </span>nalece\n                        do osb/podmiotw trzecich (innych ni Administrator)\n                    </li>\n                </ol>\n            </td>\n            <td style=\"width:148.45pt; border-right:0.75pt solid #000000; border-left:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top; background-color:#e2efd9\">\n                <p style=\"margin-top:7.2pt; margin-bottom:7.2pt; line-height:115%; font-size:9pt\">\n                    <strong><span style=\"font-family:Arial; \">Ze wzgldu na ich okres przechowywania na urzdzeniu osoby odwiedzajcej stron Sklepu Internetowego</span></strong><span\n                    style=\"font-family:Arial\">: </span>\n                </p>\n                <ol class=\"awlist2\" style=\"margin:0pt; padding-left:0pt\">\n                    <li style=\"margin-top:7.2pt; margin-left:15.1pt; margin-bottom:7.2pt; text-indent:-14.55pt; line-height:115%; font-family:Arial; font-size:9pt\">\n                        <span\n                            style=\"width:6.55pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0; </span>sesyjne\n                        (przechowywane do czasu wylogowania si ze Sklepu Internetowego lub wyczenia przegldarki\n                        internetowej) oraz\n                    </li>\n                    <li style=\"margin-top:7.2pt; margin-left:15.1pt; margin-bottom:7.2pt; text-indent:-14.55pt; line-height:115%; font-family:Arial; font-size:9pt\">\n                        <span\n                            style=\"width:6.55pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0; </span>stae\n                        (przechowywane przez okrelony czas, zdefiniowany przez parametry kadego pliku lub do czasu\n                        rcznego usunicia)\n                    </li>\n                </ol>\n            </td>\n            <td style=\"width:156.35pt; border-left:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top; background-color:#e2efd9\">\n                <p style=\"margin-top:7.2pt; margin-bottom:7.2pt; line-height:115%; font-size:9pt\">\n                    <strong><span style=\"font-family:Arial; \">Ze wzgldu na cel ich stosowania</span></strong><span\n                    style=\"font-family:Arial\">: </span>\n                </p>\n                <ol class=\"awlist3\" style=\"margin:0pt; padding-left:0pt\">\n                    <li style=\"margin-top:7.2pt; margin-left:15.05pt; margin-bottom:7.2pt; text-indent:-9.8pt; line-height:115%; font-family:Arial; font-size:9pt\">\n                        <span style=\"width:1.8pt; font:7pt \'Times New Roman\'; display:inline-block\"> </span>niezbdne\n                        (umoliwiajce prawidowe funkcjonowanie strony Sklepu Internetowego),\n                    </li>\n                    <li style=\"margin-top:7.2pt; margin-left:15.05pt; margin-bottom:7.2pt; text-indent:-9.8pt; line-height:115%; font-family:Arial; font-size:9pt\">\n                        <span style=\"width:1.8pt; font:7pt \'Times New Roman\'; display:inline-block\"> </span>funkcjonalne/preferencyjne\n                        (umoliwiajce dostosowanie strony Sklepu Internetowego do preferencji osoby odwiedzajcej\n                        stron),\n                    </li>\n                    <li style=\"margin-top:7.2pt; margin-left:15.05pt; margin-bottom:7.2pt; text-indent:-9.8pt; line-height:115%; font-family:Arial; font-size:9pt\">\n                        <span style=\"width:1.8pt; font:7pt \'Times New Roman\'; display:inline-block\"> </span>analityczne\n                        i wydajnociowe (gromadzce informacje o sposobie korzystania ze strony Sklepu Internetowego),\n                    </li>\n                    <li style=\"margin-top:7.2pt; margin-left:15.05pt; margin-bottom:7.2pt; text-indent:-9.8pt; line-height:115%; font-family:Arial; font-size:9pt\">\n                        <span style=\"width:1.8pt; font:7pt \'Times New Roman\'; display:inline-block\"> </span>marketingowe,\n                        reklamowe i spoecznociowe (zbierajce informacje o osobie odwiedzajcej stron Sklepu\n                        Internetowego w celu wywietlania tej osobie reklam, ich personalizacji, mierzeniu skutecznoci\n                        i prowadzenia innych dziaa marketingowych w tym rwnie na stronach internetowych odrbnych od\n                        strony Sklepu Internetowego, takich jak portale spoecznociowe albo inne strony nalece do\n                        tych samych sieci reklamowych co Sklep Internetowy)\n                    </li>\n                </ol>\n            </td>\n        </tr>\n    </table>\n    <h2 style=\"margin-bottom:7.2pt\">\n        <span style=\"font-family:Arial\"><strong>7.3.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">Administrator moe przetwarza dane zawarte w plikach Cookies podczas korzystania przez odwiedzajcych ze strony Sklepu Internetowego w nastpujcych konkretnych celach:</span>\n    </h2>\n    <table style=\"margin-left:35.2pt; margin-bottom:0pt; border:0.75pt solid #000000; border-collapse:collapse\">\n        <tr>\n            <td rowspan=\"6\"\n                style=\"width:145.15pt; border-right:0.75pt solid #000000; border-bottom:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top; background-color:#e2efd9\">\n                <p style=\"margin-bottom:7.2pt; line-height:115%; font-size:9pt\">\n                    <strong><span style=\"font-family:Arial; \">Cele stosowanie plikw Cookies w Sklepie Internetowym Administratora</span></strong>\n                </p>\n            </td>\n            <td style=\"width:313.5pt; border-left:0.75pt solid #000000; border-bottom:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-bottom:7.2pt; line-height:115%; font-size:9pt\">\n                    <span style=\"font-family:Arial\">identyfikacji Usugobiorcw jako zalogowanych w Sklepie Internetowym i pokazywania, e s zalogowani (pliki Cookies niezbdne)</span>\n                </p>\n            </td>\n        </tr>\n        <tr>\n            <td style=\"width:313.5pt; border-top:0.75pt solid #000000; border-left:0.75pt solid #000000; border-bottom:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-bottom:7.2pt; line-height:115%; font-size:9pt\">\n                    <span style=\"font-family:Arial\">zapamitywania Produktw dodanych do koszyka w celu zoenia Zamwienia (pliki Cookies niezbdne)</span>\n                </p>\n            </td>\n        </tr>\n        <tr>\n            <td style=\"width:313.5pt; border-top:0.75pt solid #000000; border-left:0.75pt solid #000000; border-bottom:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-bottom:7.2pt; line-height:115%; font-size:9pt\">\n                    <span style=\"font-family:Arial\">zapamitywania danych z wypenianych Formularzy Zamwienia, ankiet lub danych logowania do Sklepu Internetowego (pliki Cookies niezbdne lub/i funkcjonalne/preferencyjne)</span>\n                </p>\n            </td>\n        </tr>\n        <tr>\n            <td style=\"width:313.5pt; border-top:0.75pt solid #000000; border-left:0.75pt solid #000000; border-bottom:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-bottom:7.2pt; line-height:115%; font-size:9pt\">\n                    <span style=\"font-family:Arial\">dostosowywania zawartoci strony Sklepu Internetowego do indywidualnych preferencji Usugobiorcy (np. dotyczcych kolorw, rozmiaru czcionki, ukadu strony) oraz optymalizacji korzystania ze stron Sklepu Internetowego (pliki Cookies funkcjonalne/preferencyjne)</span>\n                </p>\n            </td>\n        </tr>\n        <tr>\n            <td style=\"width:313.5pt; border-top:0.75pt solid #000000; border-left:0.75pt solid #000000; border-bottom:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-bottom:7.2pt; line-height:115%; font-size:9pt\">\n                    <span style=\"font-family:Arial\">prowadzenia anonimowych statystyk przedstawiajcych sposb korzystania ze strony Sklepu Internetowego (pliki Cookies analityczne i wydajnociowe)</span>\n                </p>\n            </td>\n        </tr>\n        <tr>\n            <td style=\"width:313.5pt; border-top:0.75pt solid #000000; border-left:0.75pt solid #000000; padding-right:5.03pt; padding-left:5.03pt; vertical-align:top\">\n                <p style=\"margin-bottom:7.2pt; line-height:115%; font-size:9pt\">\n                    <span style=\"font-family:Arial\">wywietlania i renderowania reklam, ograniczania liczby wywietle reklam oraz ignorowania reklam, ktrych Usugobiorca nie chce oglda, mierzenia skutecznoci reklam, a take personalizacji reklam, to jest badania cech zachowania osb odwiedzajcych Sklep Internetowy poprzez anonimow analiz ich dziaa (np. powtarzajce si wizyty na okrelonych stronach, sowa kluczowe itp.) w celu stworzenia ich profilu i dostarczenia im reklam dopasowanych do ich przewidywanych zainteresowa, take wtedy kiedy odwiedzaj oni inne strony internetowe w sieci reklamowej firmy Google Ireland Ltd. oraz Facebook, tj. Meta Platforms Ireland Ltd. (pliki Cookies marketingowe, reklamowe i spoecznociowe)</span>\n                </p>\n            </td>\n        </tr>\n    </table>\n    <h2 style=\"margin-bottom:7.2pt\">\n        <span style=\"font-family:Arial\"><strong>7.4.</strong></span><span\n        style=\"width:20.44pt; font:7pt \'Times New Roman\'; display:inline-block\">&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0;&#xa0; </span><span\n        style=\"font-family:Arial\">Sprawdzenie w najpopularniejszych przegldarkach internetowych, jakie pliki Cookies (w tym okres funkcjonowania plikw Cookies oraz ich dostawca) s wysyane w 